import "./styles.css";
import Plx from 'react-plx';

export default function App() {
  return (
    <div>
      {/* Existing parallax elements */}
      <Plx
        parallaxData={[
          {
            start: 0,
            end: 700,
            easing: "ease-in",
            properties: [
              {
                startValue: 1,
                endValue: 1.6,
                property: "scale"
              }
            ]
          }
        ]}
        style={{
          position: "fixed",
          left: 0,
          top: 0,
          width: "100%",
          zIndex: 100
        }}
      >
        <img style={{ width: "100%" }} src="bg.png" alt="foreground" />
      </Plx>
      <Plx
        parallaxData={[
          {
            start: 0,
            end: 800,
            properties: [
              {
                startValue: 1,
                endValue: 1.18,
                property: "scale"
              }
            ]
          }
        ]}
        style={{  
          position: "fixed",
          left: 0,
          top: 0,
          width: "100%",
          zIndex: 50
        }}
      >
        <img style={{ width: "100%" }} src="background.jpg" alt="background" />
      </Plx>
      <Plx
        parallaxData={[
          {
            start: 0,
            end: 400,
            properties: [
              {
                startValue: 1,
                endValue: 0,
                property: "opacity"
              }
            ]
          }
        ]}
        style={{
          position: "fixed",
          left: "35%",
          top: "26vw",
          width: "30vw",
          zIndex: 150
        }}
      >
        <img
          style={{
            width: "100%"
          }}
          src="/text-img.webp"
          alt="Goonies"
        />
      </Plx>
      <div
        style={{
          position: "fixed",
          left: 0,
          top: 0,
          zIndex: 200,
          paddingTop: "56%",
          height: "400vh",
          width: "100%",
          background: "#000"
        }}
      ></div>

      {/* New parallax section */}
      <div
        style={{
          position: "relative",
          zIndex: 150,
          height: "100vh",
          backgroundColor: "#f0f0f0"
        }}
      >
        <Plx
          parallaxData={[
            {
              start: 'self',
              startOffset: '80vh',
              duration: '200vh',
              properties: [
                {
                  startValue: 0,
                  endValue: 1,
                  property: "opacity"
                },
                {
                  startValue: 1,
                  endValue: 1.5,
                  property: "scale"
                }
              ]
            }
          ]}
          style={{
            position: "absolute",
            top: "50%",
            left: "50%",
            transform: "translate(-50%, -50%)",
            width: "50vw",
            height: "50vh",
            backgroundColor: "#ffcc00",
            display: "flex",
            justifyContent: "center",
            alignItems: "center",
            borderRadius: "10px",
            boxShadow: "0 4px 8px rgba(0, 0, 0, 0.1)"
          }}
        >
          <h1 style={{ color: "#000", textAlign: "center" }}>Next Page Content</h1>
        </Plx>
      </div>
    </div>
  );
}
