import React from "react";
import "./styles.css";
import Plx from "react-plx";

export default function App() {
  return (
    <div>
      {/* Parallax layer for the foreground image */}
      <Plx
        parallaxData={[
          {
            start: 0,
            end: 700,
            easing: "ease-in",
            properties: [
              {
                startValue: 1,
                endValue: 1.6,
                property: "scale"
              }
            ]
          }
        ]}
        style={{
          position: "fixed",
          left: 0,
          top: 0,
          width: "100%",
          zIndex: 100
        }}
      >
        <img style={{ width: "100%" }} src="bg.png" alt="foreground" />
      </Plx>

      {/* Parallax layer for the background image */}
      <Plx
        parallaxData={[
          {
            start: 0,
            end: 800,
            properties: [
              {
                startValue: 1,
                endValue: 1.18,
                property: "scale"
              }
            ]
          }
        ]}
        style={{
          position: "fixed",
          left: 0,
          top: 0,
          width: "100%"
        }}
      >
        <img style={{ width: "100%" }} src="background.jpg" alt="background" />
      </Plx>

      {/* Parallax layer for the text image */}
      <Plx
        parallaxData={[
          {
            start: 0,
            end: 400,
            properties: [
              {
                startValue: 1,
                endValue: 0,
                property: "opacity"
              }
            ]
          }
        ]}
        style={{
          position: "fixed",
          left: 0,
          top: "26vw",
          width: "100%"
        }}
      >
        <img
          style={{
            width: "30vw"
          }}
          src="/text-img.webp"
          alt="Goonies"
        />
      </Plx>

      {/* First page content */}
      <div
        style={{
          position: "relative",
          top: 0,
          zIndex: 200,
          paddingTop: "56%",
          height: "200vh", // Increased height to allow scrolling
          width: "100%",
          background: "#000"
        }}
      >
        {/* Additional content can be added here */}
      </div>

      {/* Second page content with a parallax effect */}
      <Plx
        parallaxData={[
          {
            start: "self",
            duration: 600,
            properties: [
              {
                startValue: 1,
                endValue: 0.5,
                property: "scale"
              }
            ]
          }
        ]}
        style={{
          position: "relative",
          zIndex: 300,
          height: "100vh", // Height of the second page
          background: "#fff",
          paddingTop: "20px",
          textAlign: "center"
        }}
      >
        <h1>Second Page Content</h1>
        <p>This is the content of the second page with a parallax effect.</p>
        {/* Add more content as needed */}
      </Plx>
    </div>
  );
}
